var dappAddress = "n1mYNszjJi1WffRDhCT7bc1wceb4TarjQW8";
var hash = "eb2c7c78ff4fee54120c062df118992d0438d17168d801ce55f784cb50371712";
var NebPay = require("nebpay");
var nebPay = new NebPay();

function init () {
    if (typeof nebPay.simulateCall == "undefined" || typeof nebPay.call == "undefined") {
        toast('检测到你未开启星云钱包插件,请启动星云钱包插件并刷新.')
        return
    }
    get();
}

$(function () {
    init();
})
function show_detail(obj) {
    var data_name=obj.getAttribute("data-name"),
        data_birthday=obj.getAttribute("data-birthday"),
        data_gift=obj.getAttribute("data-gift");
    $('#model-name').html(data_name);
    $('#model-birthday').html(data_birthday);
    $('#model-gift').html(data_gift);
}



function get (name, value, time) {
    if (!name) {
        openToast('正在加载中请稍等...')
    }
    nebPay.simulateCall(dappAddress, "0", "get", JSON.stringify([]), {
        listener: function (res) {
            if(res.result == '' && res.execute_err == 'contract check failed') {
                editToast('合约检测失败，请检查浏览器钱包插件环境！');
                closeToast()
                return;
            }

            var data = JSON.parse(JSON.parse(res.result));
            console.log(data, 'data')
            if (name) {
                if (name === 'id') {
                    for (var i = 0; i < data.length; i++) {
                        if (data[i].id == value) {
                            renderHtml(data);
                            editToast('添加成功!');
                            closeToast();
                            $('#create-friend-group-1').modal('hide');
                        }
                    }
                }
            } else {
                renderHtml(data);
                closeToast();
            }
        }
    })
}

// 提交新项目
$('#add-friends-birthday').on('click', function () {
    var friendsNickName = $('#friendsNickName').val();
    var friendBirthday = $('#friendBirthday').val();
    var birthdayGift = $('#birthdayGift').val();
    var month=friendBirthday.split("-")[1];
    var id = Date.now() +'_'+ ~~(Math.random() * 1e6);
    if (friendsNickName && friendBirthday && birthdayGift) {
        openToast('正在提交中请稍等...');
        nebPay.call(dappAddress, "0", "set", JSON.stringify([{
            id: id,
            name: friendsNickName,
            birthday: friendBirthday,
            month: month,
            content: birthdayGift
        }]), {
            listener: function(res){
                if (res.txhash) {
                    editToast('系统正在尝试拉去信息中......请稍等 并不要操作页面');
                    timer = setInterval(function () {
                        get('id', id)
                    }, 5000)
                } else {
                    editToast('信息添加失败,请稍后再试');
                    closeToast()
                }
            }
        })
    } else {
        toast('请填写完整信息');
    }
});

