function toast (text) {
    $('#toast').html(text);
    $('#toast').show();
    setTimeout(function () {
        $('#toast').addClass('toast-show');
        showToast()
    }, 300);
}

function showToast () {
    setTimeout(function () {
        $('#toast').removeClass('toast-show');
        setTimeout(function () {
            $('#toast').hide();
        }, 2300);
    }, 3000);
}

function openToast (text) {
    $('#toast').html(text);
    $('#toast').show();
    setTimeout(function () {
        $('#toast').addClass('toast-show');
    }, 300);
}

function closeToast () {
    setTimeout(function () {
        $('#toast').removeClass('toast-show');
        setTimeout(function () {
            $('#toast').hide();
        }, 400);
    }, 1000);
}

function editToast (text) {
    $('#toast').html(text);
}
